00348     // Ensure expandedStates matches the number of places
00349     private void EnsureExpandedStates(int count)
00350     {
00351         while (expandedStates.Count < count) expandedStates.Add(true);
00352         while (expandedStates.Count > count) expandedStates.RemoveAt(expandedStates.Count - 1);
00353     }
